import { useState, useEffect } from "react";
import axios from "axios";
import Editor from "@monaco-editor/react";
import stubs from "./defaultStubs";
import "./App.css";

function App() {
    const [language, setLanguage] = useState("cpp");
    const [code, setCode] = useState("");
    const [output, setOutput] = useState("");
    const [status, setStatus] = useState("");
    const [jobId, setJobId] = useState("");
    const [editorMode, setEditorMode] = useState("vs-dark");
    const [languageIcon, setLanguageIcon] = useState("./resources/cpp.png");

    useEffect(() => {
        setCode(stubs[language]);
        setLanguageIcon(`./resources/${language}.png`);
    }, [language]);

    const toggleTheme = (idName) => {
        let currentClassName = document.getElementById(idName).className;
        let newClassName = currentClassName;
        if (currentClassName === idName + "-dark")
            newClassName = idName + "-light";
        else newClassName = idName + "-dark";
        document.getElementById(idName).className = newClassName;
    };

    const handleThemeChange = () => {
        if (editorMode === "vs-light") setEditorMode("vs-dark");
        else setEditorMode("vs-light");
        toggleTheme("header");
        toggleTheme("app-name");
        const tutorial = document.getElementById("tutorial");
        console.log(tutorial.className);
        if (tutorial.className === "btn tutorial-btn-dark")
            tutorial.className = "btn tutorial-btn-light";
        else tutorial.className = "btn tutorial-btn-dark";
        console.log(tutorial.className);
    };

    const handleSubmit = async () => {
        const payload = {
            language: language,
            code: code,
        };
        try {
            setJobId("");
            setStatus("");
            setOutput("");
            const { data } = await axios.post(
                "http://localhost:5000/run",
                payload
            );
            console.log(data);
            setJobId(data.jobId);

            let intervalId;

            intervalId = setInterval(async () => {
                const { data: dataRes } = await axios.get(
                    "http://localhost:5000/status",
                    { params: { id: data.jobId } }
                );
                const { success, job, error } = dataRes;
                console.log(dataRes);
                if (success) {
                    const { status: jobStatus, output: jobOutput } = job;
                    setStatus(jobStatus);
                    if (jobStatus === "Running") return;
                    setOutput(jobOutput);
                    clearInterval(intervalId);
                } else {
                    setStatus("Error !!! ");
                    console.error(error);
                    setOutput(error);
                    clearInterval(intervalId);
                }
            }, 1000);
        } catch ({ response }) {
            if (response) {
                const errorMessage = response.data.err.stderr;
                setOutput(errorMessage);
            } else {
                setOutput("Error connecting to server!");
            }
        }
    };

    return (
        <div className="App">
            <div id="header" className="header-dark">
                <h3 id="app-name" className="app-name-dark">
                    <i className="fas fa-solid fa-cube" aria-hidden="true"></i>
                    {/* <i className="fas fa-solid fa-terminal"></i> */}
                    &nbsp; Online Code Compiler
                </h3>
                <div className="nav-right-options">
                    <button
                        className="btn"
                        id="theme-toggle-button"
                        onClick={handleThemeChange}
                    >
                        <i
                            className="fas fa-solid fa-lightbulb theme-icon fa-2x"
                            aria-hidden="true"
                        ></i>
                    </button>

                    <button id="tutorial" className="btn tutorial-btn-dark">
                        <i
                            className="fas fa-solid fa-swatchbook tutorial-icon-dark fa-2x"
                            aria-hidden="true"
                        ></i>
                    </button>
                </div>

                <div className="header-right">
                    <select
                        value={language}
                        onChange={(e) => {
                            setLanguage(e.target.value);
                            setCode(stubs[e.target.value]);
                        }}
                    >
                        <option value={"cpp"}>C++</option>
                        <option value={"python"}>Python</option>
                    </select>
                    {/* run button */}
                    <button className="btn run-btn" onClick={handleSubmit}>
                        <i
                            className="fas fa-play fa-fade run-icon"
                            aria-hidden="true"
                        ></i>
                        &nbsp; Run
                    </button>
                </div>
            </div>

            <div className="editor">
                <Editor
                    height="100%"
                    width="100%"
                    theme={editorMode}
                    defaultLanguage={language}
                    defaultValue={code}
                    value={code}
                    onChange={(e) => setCode(e)}
                />
            </div>
            <div className="std-input-output">
                <div className="std-input">
                    <Editor
                        height="100%"
                        width="100%"
                        theme={editorMode}
                        defaultLanguage="plaintext"
                        defaultValue={"// enter input here"}
                        value={"// enter input here"}
                    />
                </div>
                <div className="std-output">
                    <Editor
                        height="100%"
                        width="100%"
                        theme={editorMode}
                        defaultLanguage="plaintext"
                        defaultValue={"// output"}
                        value={output}
                    />
                </div>
            </div>
            <br />
            {/* <button onClick={handleSubmit}>submit</button> */}
            {/* <p>{status}</p> */}
            {/* <p>{jobId && `JobID: ${jobId}`}</p> */}
        </div>
    );
}

export default App;
