import { useState, useEffect } from "react";
import axios from "axios";
import Editor from "@monaco-editor/react";
import stubs from "./defaultStubs";
import "./App.css";

function App() {
    const [language, setLanguage] = useState("cpp");
    const [code, setCode] = useState("");
    const [output, setOutput] = useState("");
    const [status, setStatus] = useState("");
    const [jobId, setJobId] = useState("");
    const [editorMode, setEditorMode] = useState("vs-dark");

    useEffect(() => {
        setCode(stubs[language]);
    }, [language]);

    const handleEditorMode = () => {
        if (editorMode === "vs-light") setEditorMode("vs-dark");
        else setEditorMode("vs-light");
    };

    const handleSubmit = async () => {
        const payload = {
            language: language,
            code: code,
        };
        try {
            setJobId("");
            setStatus("");
            setOutput("");
            const { data } = await axios.post(
                "http://localhost:5000/run",
                payload
            );
            console.log(data);
            setJobId(data.jobId);

            let intervalId;

            intervalId = setInterval(async () => {
                const { data: dataRes } = await axios.get(
                    "http://localhost:5000/status",
                    { params: { id: data.jobId } }
                );
                const { success, job, error } = dataRes;
                console.log(dataRes);
                if (success) {
                    const { status: jobStatus, output: jobOutput } = job;
                    setStatus(jobStatus);
                    if (jobStatus === "Running") return;
                    setOutput(jobOutput);
                    clearInterval(intervalId);
                } else {
                    setStatus("Error !!! ");
                    console.error(error);
                    setOutput(error);
                    clearInterval(intervalId);
                }
            }, 1000);
        } catch ({ response }) {
            if (response) {
                const errorMessage = response.data.err.stderr;
                setOutput(errorMessage);
            } else {
                setOutput("Error connecting to server!");
            }
        }
    };

    return (
        <div className="App">
            {/* <h1 className="app-header">Online Code Compiler</h1> */}

            <div className="header">
                {/* <img
                    className="logo"
                    alt="app-icon"
                    src={require("./resources/app-icon.png")}
                /> */}
                <h3 className="app-name"> &nbsp; Online Code Compiler</h3>

                <div className="header-right">
                    <label>Language: </label>
                    <select
                        value={language}
                        onChange={(e) => {
                            setLanguage(e.target.value);
                            setCode(stubs[e.target.value]);
                        }}
                    >
                        <option value={"cpp"}>C++</option>
                        <option value={"python"}>Python</option>
                    </select>
                    <button onClick={handleSubmit}>submit</button>
                    <button onClick={handleEditorMode}>
                        change editor theme
                    </button>
                    <button>dark mode</button>
                    <button>Tutorial</button>
                </div>
            </div>

            <div className="editor">
                <Editor
                    height="100%"
                    width="100%"
                    theme={editorMode}
                    defaultLanguage={language}
                    defaultValue={code}
                    value={code}
                    onChange={(e) => setCode(e)}
                />
            </div>
            <div className="std-input-output">
                <div className="std-input">
                    <Editor
                        height="100%"
                        width="100%"
                        theme={editorMode}
                        defaultLanguage="plaintext"
                        defaultValue={"// enter input here"}
                        value={"// enter input here"}
                    />
                </div>
                <div className="std-output">
                    <Editor
                        height="100%"
                        width="100%"
                        theme={editorMode}
                        defaultLanguage="plaintext"
                        defaultValue={"// output"}
                        value={output}
                    />
                </div>
            </div>
            <br />
            {/* <button onClick={handleSubmit}>submit</button> */}
            {/* <p>{status}</p> */}
            {/* <p>{jobId && `JobID: ${jobId}`}</p> */}
        </div>
    );
}

export default App;
